using System.Collections;using System.Collections.Generic;using UnityEngine;public class EnemyShotMovement : MonoBehaviour {    public GameObject gc;    public float speed;    public float lifeTime;    public int damageToGive;    void FixedUpdate()    {        Vector3 v = new Vector3(1, 0, 0);        transform.Translate(v * speed * Time.deltaTime);        lifeTime -= Time.deltaTime;        if (lifeTime <= 0)        {            Destroy(gameObject);        }    }    void OnTriggerEnter2D(Collider2D other)    {        if (other.gameObject.tag == "Player")        {            gc.gameObject.GetComponent<GameController>().hit();            Destroy(gameObject);        }        if (other.gameObject.tag == "wall")        {            Destroy(gameObject);        }    }}