using UnityEngine.SceneManagement;using UnityEngine;public class SceneController : MonoBehaviour {    public GameObject gc;    private static int sceneSaver = 0;    public Texture2D cursorTexture1;    public Texture2D cursorTexture2;    public CursorMode cursorMode = CursorMode.Auto;    public Vector2 hotSpot = Vector2.zero;    void Start()    {        OnMouseEnter();        }    public void switchScene(int x)    {        sceneSaver = x;        SceneManager.LoadScene(sceneSaver);        OnMouseEnter();    }    void OnMouseEnter()    {        if(sceneSaver == 0 || sceneSaver == 2)        {            Cursor.SetCursor(cursorTexture1, hotSpot, cursorMode);        }        else if (sceneSaver == 1)        {            Cursor.SetCursor(cursorTexture2, hotSpot, cursorMode);        }    }    void OnMouseExit()    {        Cursor.SetCursor(null, Vector2.zero, cursorMode);    }    public int returnSceneSaver()     {        return sceneSaver;    }}