using UnityEngine;public class ShotMovement : MonoBehaviour {    public float speed;    public float lifeTime;    public int damageToGive;    void FixedUpdate()    {        Vector3 v = new Vector3(1, 0, 0);        transform.Translate(v * speed * Time.deltaTime);        lifeTime -= Time.deltaTime;        if (lifeTime <= 0)        {            Destroy(gameObject);        }    }    void OnTriggerEnter2D(Collider2D other)    {        if (other.gameObject.tag == "enemy")        {            other.gameObject.GetComponent<EnemyController>().dmgEnemy(damageToGive);            Destroy(gameObject);        }        if (other.gameObject.tag == "wall")        {            Destroy(gameObject);        }        if (other.gameObject.tag == "obstacle")        {            Destroy(gameObject);        }    }}